import React, { useEffect, useRef } from 'react';
import '../styles/cover.css';
import p5 from 'p5';
import {gsap} from "gsap";

function Cover() {
    const canvasRef = useRef(null);

    useEffect(() => {
        function scrollToPosition(yPosition) {
            gsap.to(window, {
                duration: 1,
                scrollTo: {
                    y: yPosition,
                },
                ease: 'power2.inOut',
            });
        }
        const sketch = (p) => {
            let squares = [];
            let squareSize = 50;

            p.setup = () => {
                p.createCanvas(p.windowWidth, p.windowHeight).parent(canvasRef.current);
                p.noStroke();
                const horizontal_squares = p.windowWidth / squareSize;
                const vertical_squares = p.height / squareSize;
                for (let i = 0; i < horizontal_squares; i++) {
                    for (let j = 0; j < vertical_squares; j++) {
                        let x = i * squareSize;
                        let y = j * squareSize;
                        squares.push({ x, y, size: squareSize });
                    }
                }
            };

            p.draw = () => {
                p.background(220);

                for (let i = 0; i < squares.length; i++) {
                    let square = squares[i];
                    let distance = p.dist(p.mouseX, p.mouseY, square.x, square.y);
                    let size = p.map(distance, 0, p.width, 5, 30);
                    p.fill(255, 0, 0);
                    p.rect(square.x, square.y, size, size);
                }
            };
        };

        new p5(sketch);

    }, []);

    return (
        <div className="cover">
            <div className="cover_animation" ref={canvasRef}>
                <div className="cover_animation_title">
                    <h1 className="cover_animation_title_text">Corentin Albertus</h1>
                </div>
                <button className="cover_animation_button">temporaire</button>
            </div>
        </div>
    );
}

export default Cover;
