import React, { useEffect } from 'react';
import './../styles/header.css';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

function Header() {
    gsap.registerPlugin(ScrollTrigger);

    useEffect(() => {
        const main_header = document.querySelector('.main_header');
        const second_header = document.querySelector('.second_header');
        const logo = document.querySelector('.logo');

        // Animation de l'en-tête en réponse au défilement
        gsap.to(main_header, {
            scrollTrigger: {
                trigger: main_header,
                start: '10px',
                end: '10px',
                scrub: 1,
                markers: true,
            },
            borderRadius: '20px',
            scale: 0.9,
        });

        gsap.to(second_header, {
            scrollTrigger: {
                trigger: second_header,
                start: '10px',
                end: '10px',
                scrub: false,
                markers: false,
                toggleActions: "restart none none none",
                onEnterBack: () => {
                    gsap.to(second_header, {
                        opacity: 1,
                        duration: 1,
                    });
                },
            },
            opacity: 0,
            duration: 1,
        });

        // Animation de rotation du logo au survol
        logo.addEventListener('mouseenter', () => {
            gsap.to(logo, { rotation: 20, duration: 0.5 });
        });

        logo.addEventListener('mouseleave', () => {
            gsap.to(logo, { rotation: 0, duration: 0.5 });
        });
    }, []);

    return (
        <header className="header">
            <div className="second_header">
                <nav>
                    <ul>
                        <li>
                            <a href="https://discord.com/">
                                <img className="logo_discord" src="https://upload.wikimedia.org/wikipedia/fr/thumb/4/4f/Discord_Logo_sans_texte.svg/1818px-Discord_Logo_sans_texte.svg.png" alt="discord" width={20} height={55}/>
                            </a>
                        </li>
                        <li>
                            <a href="https://www.instagram.com/">
                                <img className="logo_instagram" src="../assets/github-logo.png" alt="instagram"/>
                            </a>
                        </li>
                        <li>
                            <a href="https://slack.com/">
                                <img className="logo_slack" src="../assets/instagram-logo.png" alt="slack"/>
                            </a>
                        </li>
                        <li>
                            <a href="https://github.com/">
                                <img className="logo_github" src="../assets/slack-logo.png" alt="github"/>
                            </a>
                        </li>
                    </ul>

                </nav>
            </div>
            <div className="main_header">
                <nav>
                    <img className="logo" src="https://cryptologos.cc/logos/uniswap-uni-logo.png" alt={"logo"}/>
                    <ul>
                        <li className="hover-animation"><a href="/">Accueil</a></li>
                        <li className="hover-animation"><a href="/a-propos">À propos</a></li>
                        <li className="hover-animation"><a href="/services">Services</a></li>
                        <li className="hover-animation"><a href="/contact">Contact</a></li>
                    </ul>
                </nav>
            </div>
        </header>
    );
}

export default Header;
