import React from 'react';
import './../styles/header.css';
import { gsap } from 'gsap';

function Header() {
    // Fonction pour l'animation de survol
    const handleMouseEnter = (event) => {
        gsap.to(event.target, {
            scale: 1.1, // La valeur par laquelle l'élément est agrandi
            duration: 0.3, // Durée de l'animation
        });
    };

    // Fonction pour réinitialiser l'animation lorsque la souris quitte l'élément
    const handleMouseLeave = (event) => {
        gsap.to(event.target, {
            scale: 1, // Réinitialiser l'échelle à la valeur d'origine
            duration: 0.3, // Durée de l'animation
        });
    };

    return (
        <header>
            <nav>
                <div className="logo"></div>
                <ul>
                    <li>
                        <a href="/" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
                            Accueil
                        </a>
                    </li>
                    <li>
                        <a href="/a-propos" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
                            À propos
                        </a>
                    </li>
                    <li>
                        <a href="/services" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
                            Services
                        </a>
                    </li>
                    <li>
                        <a href="/contact" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
                            Contact
                        </a>
                    </li>
                </ul>
            </nav>
        </header>
    );
}

export default Header;
