import React, { useEffect } from 'react';
import './../styles/header.css';
import { gsap } from 'gsap';

function Header() {
    useEffect(() => {
        const liElements = document.querySelectorAll('.hover-animation');

        liElements.forEach((element) => {
            gsap.to(element, {
                opacity: 0.7,
                duration: 0.3,
                ease: 'power1.inOut',
                onStart: () => {
                    element.style.cursor = 'pointer'; // Change le curseur au survol
                },
            });

            gsap.to(element, {
                opacity: 1,
                duration: 0.3,
                ease: 'power1.inOut',
                onComplete: () => {
                    element.style.cursor = 'default'; // Rétablit le curseur lorsque la souris quitte l'élément
                },
                onReverseComplete: () => {
                    element.style.cursor = 'default'; // Rétablit le curseur lorsque l'animation est inversée
                },
                paused: true, // L'animation est mise en pause par défaut
            });

            element.addEventListener('mouseenter', () => {
                gsap.fromTo(element, { opacity: 0.7 }, { opacity: 1 });
                gsap.to(element, { cursor: 'pointer' });
                gsap.play(element); // Lance l'animation au survol
            });

            element.addEventListener('mouseleave', () => {
                gsap.reverse(element); // Inverse l'animation lorsque la souris quitte l'élément
            });
        });
    }, []);

    return (
        <header>
            <nav>
                <div className="logo"></div>
                <ul>
                    <li className="hover-animation"><a href="/">Accueil</a></li>
                    <li className="hover-animation"><a href="/a-propos">À propos</a></li>
                    <li className="hover-animation"><a href="/services">Services</a></li>
                    <li className="hover-animation"><a href="/contact">Contact</a></li>
                </ul>
            </nav>
        </header>
    );
}

export default Header;
