function getRandomCharacter() {
    const characters = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    const randomIndex = Math.floor(Math.random() * characters.length);
    return characters.charAt(randomIndex);
}

function animationTextRandom() {
    const words = ["Hello World", "Hello World", "Hello World", "Hello World"];
    const text = document.querySelector(".content_title");
    let currentWordIndex = 0;
    let count = 0;
    const currentWord = words[currentWordIndex];

    function updateText() {
        let newContent = '';

        for (let i = 0; i < currentWord.length; i++) {
            if (i === count) {
                newContent += getRandomCharacter();
            } else {
                newContent += currentWord[i];
            }
        }

        text.textContent = newContent;

        count++;
        if (count === currentWord.length) {
            count = 0;
            currentWordIndex = (currentWordIndex + 1) % words.length;
        }

        setTimeout(updateText, 75); // Répéter l'animation avec un délai
    }


    updateText(); // Démarrer l'animation
}

export { animationTextRandom };
