import React, { useEffect, useRef } from 'react';
import '../styles/cover.css';
import p5 from 'p5';
import { gsap } from 'gsap';
import { ScrollToPlugin } from 'gsap/ScrollToPlugin';
import lottie from 'lottie-web';
import animationData from '../assets/scroll_down.json';
import { animationTextRandom } from '../animation/text_random';

gsap.registerPlugin(ScrollToPlugin);

function Cover() {
    const canvasRef = useRef(null); // Add a canvas ref

    useEffect(() => {
        const sub = document.querySelectorAll('.content_subtitle_name');
        const animationContainer = document.getElementById('your-lottie-animation');
        const anim = lottie.loadAnimation({
            container: animationContainer,
            renderer: 'svg',
            loop: true,
            autoplay: true,
            animationData: animationData,
        });
        const box = document.querySelector('.cover');

        gsap.to(box, {
            duration: 2,
            background: 'linear-gradient(to right, #000E00, #222222)',
            repeat: -1,
            yoyo: true,
        });

        let currentIndex = 0;

        function changeText() {
            // Masquez l'élément précédent
            if (sub[currentIndex - 1]) {
                gsap.to(sub[currentIndex - 1], {
                    display: 'none',
                    duration: 1,
                    y: -100, // Animation de sortie
                    opacity: 0,
                    position: 'relative',
                });
            }

            // Récupérez le dernier mot
            const words = sub[currentIndex].textContent.split(' ');
            const lastWord = words[words.length - 1];

            // Changez la couleur du dernier mot
            sub[currentIndex].innerHTML = words.slice(0, -1).join(' ') + ' <span class="color-changing">' + lastWord + '</span>';

            // Animation de changement de couleur
            gsap.to('.color-changing', {
                duration: 2,
                color: '#fff',
                repeat: 1,
                yoyo: true,
                onComplete: () => {
                    // Passez à l'élément suivant
                    currentIndex = (currentIndex + 1) % sub.length;

                    // Affichez l'élément suivant avec une animation d'entrée
                    gsap.from(sub[currentIndex], {
                        y: 100, // Animation d'entrée
                        duration: 1,
                        opacity: 0,
                        display: 'block',
                        position: 'relative',
                    });

                    // Répétez le processus toutes les 2 secondes
                    setTimeout(changeText, 2000);
                },
            });
        }

// Commencez le processus en affichant le premier élément
        sub[currentIndex].style.display = 'block';
        changeText();


// Commencez le processus en affichant le premier élément
        sub[currentIndex].style.display = 'block';
        changeText();
    }, []);

    return (
        <div className="cover">
            <div className="cover_animation" ref={canvasRef}></div>
            <div className="content">
                <div className="content_box">
                    <h1 className="content_title">Hello,</h1>
                    <h2 className="content_title">I'm <span>Corentin</span></h2>
                    <h2 className="content_title">I'm <span className="content_subtitle_name">Corentin</span>
                        <span className="content_subtitle_name">
                            Developer
                        </span>
                        <span className="content_subtitle_name">
                            Front-End
                        </span>
                        <span className="content_subtitle_name">
                            React
                        </span>
                    </h2>
                    <button className="content_button">Voir plus</button>
                </div>
            </div>
        </div>
    );
}

export default Cover;
