import React, { useEffect } from 'react';
import './../styles/header.css'; // Utilisez simplement 'import './Header.css' pour importer le fichier de style.
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

function Header() {
    gsap.registerPlugin(ScrollTrigger);

    useEffect(() => {
        const header = document.querySelector('main_header');
        const logo = document.querySelector('.logo');

        // Animation de l'en-tête en réponse au défilement
        gsap.to(header, {
            scrollTrigger: {
                trigger: header,
                start: '10px',
                end: '10px',
                scrub: 1,
                markers: true,
            },
            marginTop: '20px',
            borderRadius: '20px 20px 20px 20px',
            scale: 0.9,
        });

        // Animation de rotation du logo au survol
        logo.addEventListener('mouseenter', () => {
            gsap.to(logo, { rotation: 20, duration: 0.5 });
        });

        logo.addEventListener('mouseleave', () => {
            gsap.to(logo, { rotation: 0, duration: 0.5 });
        });
    }, []);

    return (
        <header className="header">
            <nav className="second_header">
                <ul>
                </ul>
            </nav>
            <nav className="main_header">
                <img className="logo" src="https://cryptologos.cc/logos/uniswap-uni-logo.png" alt={"logo"}/>
                <ul>
                    <li className="hover-animation"><a href="/">Accueil</a></li>
                    <li className="hover-animation"><a href="/a-propos">À propos</a></li>
                    <li className="hover-animation"><a href="/services">Services</a></li>
                    <li className="hover-animation"><a href="/contact">Contact</a></li>
                </ul>
            </nav>
        </header>
    );
}

export default Header;
