import React, { useEffect } from 'react';
import '../styles/sectionSeparator.css';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

gsap.registerPlugin(ScrollTrigger);

function SectionSeparator() {
    useEffect(() => {
        const separator = document.querySelector('.cover');
        const newSection = document.querySelector('.project'); // Remplacez par le sélecteur approprié

        gsap.to(separator, {
            scrollTrigger: {
                trigger: separator,
                start: 'top top',
                end: 'bottom top',
                scrub: true,
                pin: true,
            },
        });

        gsap.to(newSection, {
            scrollTrigger: {
                trigger: separator,
                start: 'top top',
                end: 'bottom top',
                scrub: true,
            },
            marginTop: '100vh',
        });
    }, []);

    return (
        <div className="section_separator">

        </div>
    );
}

export default SectionSeparator;
