import React, { useEffect } from 'react';
import { gsap } from 'gsap';
import Header from './componants/header';
import Footer from './componants/footer';
import Project from "./componants/project";
import Cover from "./componants/cover";
import './styles/app.css';

function App() {
    useEffect(() => {
        const follower = document.querySelector('.mouse_following');
        document.body.style.cursor = 'none';
        function followMouse() {
            gsap.to(follower, {
                x: event.clientX - follower.offsetWidth / 2 + window.scrollX,
                y: event.clientY - follower.offsetHeight / 2 + window.scrollY,
                duration: 0.1,
                ease: 'power2.out',
            });

            requestAnimationFrame(followMouse);
        }

        followMouse();
    }, []);

    return (
        <div className="App">
            <div className="mouse_following"></div>
            <Header />
            <Cover />
            <Project />
            <Footer />
        </div>
    );
}

export default App;
