function getRandomCharacter() {
    const characters = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    const randomIndex = Math.floor(Math.random() * characters.length);
    return characters.charAt(randomIndex);
}

function animationTextRandom(text) {
    let  words = ["Hello World", "Hello World", "Hello World", "Hello World"];

    for (let i = 0; i < words.length; i++) {
        for (let j = 0; j < text.textContent.length * 3; j++) {
            if (j > (text.textContent.length * 2)) {
                text.textContent[j] = words[i][j];
            }
            text.textContent[j] = getRandomCharacter();
        }
    }
}

export { animationTextRandom };
