import React, { useRef, useEffect } from 'react';
import p5 from 'p5'; // Importez la bibliothèque p5.js

function AnimationCanvas() {
    const canvasRef = useRef();

    useEffect(() => {
        const sketch = (p) => {
            let squares = []; // Tableau pour stocker les carrés
            let squareSize = 50; // Taille initiale du carré

            p.setup = () => {
                p.createCanvas(400, 400);
                p.noStroke();

                // Créez plusieurs carrés avec des positions aléatoires
                for (let i = 0; i < 5; i++) {
                    let x = p.random(p.width);
                    let y = p.random(p.height);
                    squares.push({ x, y, size: squareSize });
                }
            };

            p.draw = () => {
                p.background(220);

                for (let i = 0; i < squares.length; i++) {
                    let square = squares[i];

                    // Dessinez les carrés en utilisant leurs propriétés x, y et size
                    p.fill(255, 0, 0);
                    p.rect(square.x, square.y, square.size, square.size);
                }
            };
        };

        new p5(sketch);

    }, []);

    return (
        <div className="cover">
            <div ref={canvasRef}></div>
        </div>
    );
}

export default AnimationCanvas;
