import {gsap} from "gsap";

function scrollToPosition(yPosition) {
    gsap.to(window, {
        duration: 1, // Durée de l'animation en secondes
        scrollTo: {
            y: yPosition,
        },
        ease: 'power2.inOut', // Facilité d'animation (choisissez celle qui vous convient)
    });
}

export default scrollToPosition;
