import React, { useEffect, useRef } from 'react';
import './../styles/header.css';
import { gsap } from 'gsap';

function Header() {
    const liElementsRef = useRef([]);

    useEffect(() => {
        const logo = document.querySelector('.logo');

        gsap.to(header, {
            scrollTrigger: {
                trigger: header,
                start: 'top top', // Déclencher l'animation lorsque le haut de l'en-tête atteint le haut de la fenêtre
                end: 'bottom top', // Arrêter l'animation lorsque le haut de l'en-tête atteint le haut de la fenêtre
                scrub: 1, // Permet de suivre le défilement de manière fluide
            },
            width: '90%', // Animer la largeur à 90%
            marginLeft: '5%', // Animer la marge gauche à 5%
            marginRight: '5%', // Animer la marge droite à 5%
        });

        logo.addEventListener('mouseenter', () => {
            gsap.to(logo, { rotation: 20, duration: 0.5 });
        });

        logo.addEventListener('mouseleave', () => {
            gsap.to(logo, { rotation: 0, duration: 0.5 });
        });
    }, []);

    return (
        <header className="main_header">
            <nav>
                <div className="logo"></div>
                <ul>
                    <li className="hover-animation" ref={liElementsRef.current[0]}><a href="/">Accueil</a></li>
                    <li className="hover-animation" ref={liElementsRef.current[1]}><a href="/a-propos">À propos</a></li>
                    <li className="hover-animation" ref={liElementsRef.current[2]}><a href="/services">Services</a></li>
                    <li className="hover-animation" ref={liElementsRef.current[3]}><a href="/contact">Contact</a></li>
                </ul>
            </nav>
        </header>
    );
}

export default Header;
