import React, { useRef, useEffect } from 'react';
import p5 from 'p5'; // Importez la bibliothèque p5.js

function AnimationCanvas() {
    const canvasRef = useRef();

    useEffect(() => {
        const sketch = (p) => {
            let squareSize = 50; // Taille initiale du carré

            p.setup = () => {
                p.createCanvas(400, 400);
                p.noStroke();
            };

            p.draw = () => {
                p.background(220);

                // Calculer la distance entre la souris et le centre du canvas
                let d = p.dist(p.width / 2, p.height / 2, p.mouseX, p.mouseY);

                // Plus la souris est proche, plus le carré est grand
                let newSize = p.map(d, 0, p.width, 50, 200);

                // Lissage pour une animation douce
                squareSize += (newSize - squareSize) * 0.1;

                // Dessin du carré
                p.fill(255, 0, 0);
                p.rect(p.width / 2, p.height / 2, squareSize, squareSize);
            };
        };
        new p5(sketch);

    }, []);

    return (
        <div className="cover">
            <div ref={canvasRef}></div>
        </div>
    );
}

export default AnimationCanvas;
