import React, { useEffect, useRef } from 'react';
import './../styles/header.css';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

function Header() {
    const liElementsRef = useRef([]);
    gsap.registerPlugin(ScrollTrigger);

    useEffect(() => {
        const logo = document.querySelector('.logo');
        const header = document.querySelector('header');

        gsap.to(header, {
            scrollTrigger: {
                trigger: header,
                start: 'top top',
                end: 'bottom top',
                scrub: 1,
            },
            width: '90%',
            marginLeft: '5%',
            marginRight: '5%',
        });

        logo.addEventListener('mouseenter', () => {
            gsap.to(logo, { rotation: 20, duration: 0.5 });
        });

        logo.addEventListener('mouseleave', () => {
            gsap.to(logo, { rotation: 0, duration: 0.5 });
        });
    }, []);

    return (
        <header className="header">
            <nav>
                <div className="logo"></div>
                <ul>
                    <li className="hover-animation" ref={liElementsRef.current[0]}><a href="/">Accueil</a></li>
                    <li className="hover-animation" ref={liElementsRef.current[1]}><a href="/a-propos">Ã€ propos</a></li>
                    <li className="hover-animation" ref={liElementsRef.current[2]}><a href="/services">Services</a></li>
                    <li className="hover-animation" ref={liElementsRef.current[3]}><a href="/contact">Contact</a></li>
                </ul>
            </nav>
        </header>
    );
}

export default Header;
