import React, { useEffect, useRef } from 'react';
import './../styles/header.css';
import { gsap } from 'gsap';

function Header() {
    const liElementsRef = useRef([]);

    useEffect(() => {
        // Créez un tableau de références pour les éléments li
        liElementsRef.current = liElementsRef.current.slice(0, 4).map((_, i) => liElementsRef.current[i] || createRef());

        liElementsRef.current.forEach((element, index) => {
            const liElement = element.current;

            gsap.to(liElement, {
                opacity: 0,
                duration: 0.3,
                ease: 'power1.inOut',
                onStart: () => {
                    liElement.style.cursor = 'pointer';
                },
            });

            gsap.to(liElement, {
                opacity: 1,
                duration: 0.3,
                ease: 'power1.inOut',
                onComplete: () => {
                    liElement.style.cursor = 'default';
                },
                paused: true,
            });

            liElement.addEventListener('mouseenter', () => {
                gsap.to(liElement, { opacity: 1 });
                gsap.to(liElement, { cursor: 'pointer' });
                gsap.play();
            });

            liElement.addEventListener('mouseleave', () => {
                gsap.to(liElement, { opacity: 0.7 });
                gsap.to(liElement, { cursor: 'default' });
                gsap.reverse();
            });
        });
    }, []);

    return (
        <header>
            <nav>
                <div className="logo"></div>
                <ul>
                    <li className="hover-animation" ref={liElementsRef.current[0]}><a href="/">Accueil</a></li>
                    <li className="hover-animation" ref={liElementsRef.current[1]}><a href="/a-propos">À propos</a></li>
                    <li className="hover-animation" ref={liElementsRef.current[2]}><a href="/services">Services</a></li>
                    <li className="hover-animation" ref={liElementsRef.current[3]}><a href="/contact">Contact</a></li>
                </ul>
            </nav>
        </header>
    );
}

export default Header;
