import React, { useEffect } from 'react';
import { gsap } from 'gsap';
import Header from './componants/header';
import Footer from './componants/footer';
import Project from "./componants/project";
import Cover from "./componants/cover";
import './styles/app.css';

// Importez votre fichier de configuration Particles.js
import particlesConfig from "./animation/particles-config";

function App() {
    useEffect(() => {
        function scrollToPosition(yPosition) {
            gsap.to(window, {
                duration: 1, // Durée de l'animation en secondes
                scrollTo: {
                    y: yPosition,
                },
                ease: 'power2.inOut', // Facilité d'animation (choisissez celle qui vous convient)
            });
        }
        // Initialisation de l'animation Particles.js
        if (window.particlesJS) {
            window.particlesJS("particles-js", particlesConfig);
        }
    }, []);

    return (
        <div className="App">
            <Header />
            <Cover />
            <Project />
            <Footer />
        </div>
    );
}

export default App;
