// ParticlesBackground.js
import React, { useEffect } from 'react';

const ParticlesBackground = () => {
    useEffect(() => {
        particlesJS("particles-container", {
            particles: {
                number: {
                    value: 100, // Nombre de particules
                    density: {
                        enable: true,
                        value_area: 800,
                    },
                },
                color: {
                    value: "#000", // Couleur des particules
                },
                shape: {
                    type: "circle", // Forme des particules (peut être "circle", "edge", "triangle", etc.)
                    stroke: {
                        width: 0,
                        color: "#000",
                    },
                    polygon: {
                        nb_sides: 5,
                    },
                },
                opacity: {
                    value: 0.5, // Opacité des particules
                    random: true,
                },
                size: {
                    value: 5, // Taille des particules
                    random: true,
                },
                move: {
                    enable: true,
                    speed: 1, // Vitesse de déplacement des particules
                    direction: "none", // Direction du mouvement ("none", "top", "top-right", "right", "bottom-right", "bottom", "bottom-left", "left", "top-left")
                    random: true,
                    straight: false,
                    out_mode: "out",
                    bounce: false,
                    attract: {
                        enable: false,
                        rotateX: 600,
                        rotateY: 1200,
                    },
                },
            },
            interactivity: {
                detect_on: "canvas",
                events: {
                    onhover: {
                        enable: true,
                        mode: "repulse", // Réaction au survol de la souris ("grab", "bubble", "repulse", "connect")
                    },
                    onclick: {
                        enable: true,
                        mode: "push", // Réaction au clic de souris ("push", "remove", "bubble", "repulse")
                    },
                    resize: true,
                },
            },
            retina_detect: true,
        });
    }, []);
    return <div id="particles-container"></div>;
};

export default ParticlesBackground;
