import React, {useEffect} from 'react';
import './../styles/cover.css';
import AnimationCanvas from '../animation/animation-cover'; // Importez le composant AnimationCanvas
import p5 from 'p5';

function Cover() {

    useEffect(() => {
        const sketch = (p) => {
            let squares = [];
            let squareSize = 50;

            p.setup = () => {
                p.createCanvas(p.windowWidth, 500);
                p.noStroke();
                const horizontal_squares = p.windowWidth / squareSize;
                const vertical_squares = p.windowHeight / squareSize;
                for (let i = 0; i < horizontal_squares; i++) {
                    for (let j = 0; j < vertical_squares; j++) {
                        let x = i * squareSize;
                        let y = j * squareSize;
                        squares.push({ x, y, size: squareSize });
                    }
                }
            };

            p.draw = () => {
                p.background(220);

                for (let i = 0; i < squares.length; i++) {
                    let square = squares[i];
                    let distance = p.dist(p.mouseX, p.mouseY, square.x, square.y);
                    let size = p.map(distance, 0, p.width, 5, 50);
                    p.fill(255, 0, 0);
                    p.rect(square.x, square.y, size, size);
                }
            };
        };

        new p5(sketch);

    }, []);
    return (
        <div className="cover">
            <div className="cover_animation">
                <AnimationCanvas />
            </div>
        </div>
    );
}

export default Cover;
