import React, { useEffect } from 'react';
import './../styles/header.css'; // Utilisez simplement 'import './Header.css' pour importer le fichier de style.
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

function Header() {
    gsap.registerPlugin(ScrollTrigger);

    useEffect(() => {
        const header = document.querySelector('header');
        const logo = document.querySelector('.logo');
        const liElements = document.querySelectorAll('.hover-animation');

        // Animation de l'en-tête en réponse au défilement
        gsap.from(header, {
            scrollTrigger: {
                trigger: header,
                start: '300px',
                end: '800px',
                scrub: 1,
                markers: true,
            },
            scale: 0.8,
        });

        // Animation de rotation du logo au survol
        logo.addEventListener('mouseenter', () => {
            gsap.to(logo, { rotation: 20, duration: 0.5 });
        });

        logo.addEventListener('mouseleave', () => {
            gsap.to(logo, { rotation: 0, duration: 0.5 });
        });

        // Animation au survol des éléments de la liste
        liElements.forEach((li) => {
            li.addEventListener('mouseenter', () => {
                gsap.to(li, { scale: 1.2, duration: 0.5 });
            });

            li.addEventListener('mouseleave', () => {
                gsap.to(li, { scale: 1, duration: 0.5 });
            });
        });
    }, []);

    return (
        <header className="header">
            <nav>
                <div className="logo"></div>
                <ul>
                    <li className="hover-animation"><a href="/">Accueil</a></li>
                    <li className="hover-animation"><a href="/a-propos">À propos</a></li>
                    <li className="hover-animation"><a href="/services">Services</a></li>
                    <li className="hover-animation"><a href="/contact">Contact</a></li>
                </ul>
            </nav>
        </header>
    );
}

export default Header;
