function getRandomCharacter() {
    const characters = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    const randomIndex = Math.floor(Math.random() * characters.length);
    return characters.charAt(randomIndex);
}

function animationTextRandom(text) {
    const words = ["Hello World", "Hello World", "Hello World", "Hello World"];

    // Crée un tableau pour stocker les caractères aléatoires
    const randomChars = Array.from({ length: text.textContent.length }, () => getRandomCharacter());

    for (let i = 0; i < words.length; i++) {
        // Clone le tableau randomChars pour éviter de modifier le texte initial
        const animatedText = [...randomChars];

        for (let j = 0; j < text.textContent.length; j++) {
            if (j > (text.textContent.length - 1) * 2) {
                animatedText[j] = words[i][j - (text.textContent.length - 1) * 2];
            }
        }

        // Affiche le texte animé
        text.textContent = animatedText.join('');
    }
}


export { animationTextRandom };
