import React, { useEffect } from 'react';
import './../styles/project.css';
import { gsap } from 'gsap';
import { ScrollTrigger} from 'gsap/ScrollTrigger';
import { scrollToPosition } from '../utils/scroll';
gsap.registerPlugin(ScrollTrigger);

function Project() {
    useEffect(() => {
        const boxes = document.querySelectorAll('.container');
        const scrollHorizontalElement = document.querySelector('.project_box');

        const horizontalAnimation = gsap.from(scrollHorizontalElement, {
            x: 2000,
            ease: 'none',
        });

        boxes.forEach((box) => {
            gsap.from(box, {
                scrollTrigger: {
                    trigger: box,
                    scrub: 1,
                    start: 'top',
                    end: 'bottom',
                    markers: true,
                    toggleActions: 'restart none none none',
                },
                trigger: box,
                x: 2000,
                ease: 'none',
            });
        });

        ScrollTrigger.create({
            trigger: scrollHorizontalElement,
            start: 'top top',
            end: 'bottom top',
            animation: horizontalAnimation,
            scrub: true,
        });
    }, []);

    return (
        <div className="project">
            <div className="container">
                <div className="project_image_title">Hac ex causa conlaticia stipe Valerius humatur ille Publicola et subsidiis amicorum mariti inops cum liberis uxor alitur Reguli et dotatur ex aerario filia Scipionis, cum nobilitas florem adultae virginis diuturnum absentia pauperis erubesceret patris.</div>
                <img src="https://th.bing.com/th/id/OIG.jFBs3qXz4ADuUkgdmy2S?w=270&h=270&c=6&r=0&o=5&pid=ImgGn" className="img_project" alt="Votre image" />
            </div>
            <div className="container">
                <div className="project_image_title">Hac ex causa conlaticia stipe Valerius humatur ille Publicola et subsidiis amicorum mariti inops cum liberis uxor alitur Reguli et dotatur ex aerario filia Scipionis, cum nobilitas florem adultae virginis diuturnum absentia pauperis erubesceret patris.</div>
                <img src="https://th.bing.com/th/id/OIG.jFBs3qXz4ADuUkgdmy2S?w=270&h=270&c=6&r=0&o=5&pid=ImgGn" className="img_project" alt="Votre image" />
            </div>
            <div className="container">
                <div className="project_image_title">Hac ex causa conlaticia stipe Valerius humatur ille Publicola et subsidiis amicorum mariti inops cum liberis uxor alitur Reguli et dotatur ex aerario filia Scipionis, cum nobilitas florem adultae virginis diuturnum absentia pauperis erubesceret patris.</div>
                <img src="https://th.bing.com/th/id/OIG.jFBs3qXz4ADuUkgdmy2S?w=270&h=270&c=6&r=0&o=5&pid=ImgGn" className="img_project" alt="Votre image" />
            </div>
        </div>
    );
}

export default Project;
