import React, { useEffect, useRef } from 'react';
import '../styles/cover.css';
import p5 from 'p5';
import { gsap } from "gsap";
import ScrollToPlugin from 'gsap/ScrollToPlugin';
import { scrollToPosition } from '../utils/scroll';

function Cover() {
    const canvasRef = useRef(null);

    useEffect(() => {
        gsap.registerPlugin(ScrollToPlugin);
        let lastScrollTop = 0;
        const cover = document.querySelector('.cover');

        const sketch = (p) => {
            let squares = [];
            let squareSize = 50;

            p.setup = () => {
                p.createCanvas(p.windowWidth, p.windowHeight).parent(canvasRef.current);
                p.noStroke();
                const horizontal_squares = p.windowWidth / squareSize;
                const vertical_squares = p.height / squareSize;
                for (let i = 0; i < horizontal_squares; i++) {
                    for (let j = 0; j < vertical_squares; j++) {
                        let x = i * squareSize;
                        let y = j * squareSize;
                        squares.push({ x, y, size: squareSize });
                    }
                }
            };

            p.draw = () => {
                p.background(220);

                for (let i = 0; i < squares.length; i++) {
                    let square = squares[i];
                    let distance = p.dist(p.mouseX, p.mouseY, square.x, square.y);
                    let size = p.map(distance, 0, p.width, 5, 30);
                    p.fill(255, 0, 0);
                    p.rect(square.x, square.y, size, size);
                }
            };
        };

        new p5(sketch);

        cover.addEventListener("scroll", function(){ // or window.addEventListener("scroll"....
            let st = document.documentElement.scrollTop;
            if (st > lastScrollTop){
                // downscroll code
                scrollToPosition(window.scrollHeight);
            } else {
                // upscroll code
                scrollToPosition(0)
            }
            lastScrollTop = st;
        }, false);

    }, []);

    return (
        <div className="cover">
            <div className="cover_animation" ref={canvasRef}>
            </div>
        </div>
    );
}

export default Cover;
