import React, { useRef, useEffect } from 'react';
import p5 from 'p5'; // Importez la bibliothèque p5.js

function AnimationCanvas() {
    const canvasRef = useRef();

    useEffect(() => {
        const sketch = (p) => {
            let squares = [];
            let squareSize = 50;

            p.setup = () => {
                p.createCanvas(1920, 1080);
                p.noStroke();
                const horizontal_squares = p.windowWidth / squareSize;
                const vertical_squares = p.windowHeight / squareSize;
                for (let i = 0; i < horizontal_squares; i++) {
                    for (let j = 0; j < vertical_squares; j++) {
                        let x = i * squareSize;
                        let y = j * squareSize;
                        squares.push({ x, y, size: squareSize });
                    }
                }
            };

            p.draw = () => {
                p.background(220);

                for (let i = 0; i < squares.length; i++) {
                    let square = squares[i];
                    let test = random(0, 255)
                    // Dessinez les carrés en utilisant leurs propriétés x, y et size
                    p.fill(test, 0, 0);
                    p.rect(square.x, square.y, square.size, square.size);
                }
            };
        };

        new p5(sketch);

    }, []);

    return (
        <div className="cover">
            <div ref={canvasRef}></div>
        </div>
    );
}

export default AnimationCanvas;
